// packages/client/src/store/gameStore.ts
import { create } from 'zustand';
import { Coord } from '@cumor/shared';

interface GameStore {
  hoveredHex: Coord | null;
  selectedHex: Coord | null;

  setHoveredHex: (hex: Coord | null) => void;
  setSelectedHex: (hex: Coord | null) => void;
}

export const useGameStore = create<GameStore>((set) => ({
  hoveredHex: null,
  selectedHex: null,

  setHoveredHex: (hex) => set({ hoveredHex: hex }),
  setSelectedHex: (hex) => set({ selectedHex: hex }),
}));